mapper.select:
SELECT id 
FROM users
WHERE id=#{id}

SELECT id, pwd, email, postcode, roadAddr, detailAddr, extraAddr, regdate
FROM users
WHERE id=#{id }

mapper.update:
UPDATE users
SET pwd=#{newPwd }
WHERE id=#{id }

UPDATE users
SET email=#{email }, postcode=#{postcode }, roadAddr=#{roadAddr }, detailAddr=#{detailAddr }, extraAddr=#{extraAddr  }
WHERE id=#{id }
